class BoxScanner
{
    constructor(y, x) {
        this.startY = y;
        this.startX = x;

        this.y = -1;
        this.x = -1;
    }

    begin () {
        this.y = this.startY;
        this.x = this.startX;
    }

    next () {
        if (this.x < this.startX + 3) {
            this.x++;
        }
        else if (this.y < this.startY + 3) {
            this.y++;
            this.x = this.startX;
        }
    }

    end () {
        if (this.x >= this.startX + 3 && this.y >= this.startY + 3) {
            return true;
        }
        else {
            return false;
        }
    }
}

export { BoxScanner };